    def Kullanici_Ekle_Fonskiyon(self):
        try:
            imlec.execute("""SELECT kullanici_ID FROM kullanici_bilgileri""")
            d = imlec.fetchall()
            tuple_ID = tuple([self.id_ekle.text()])
            print(tuple_ID)
            if tuple_ID not in d:
                veriler = [str(self.ad_ekle.text()),str(self.soyad_ekle.text()),self.kullaniciID]
                imlec.execute("""INSERT INTO kullanici_bilgileri VALUES(?,?,?) """,veriler)
                db.commit()
        except:
            print("Hata 1")

    def Arac_Ekle_Fonskiyon(self):
        try:
            imlec.execute("""SELECT plaka FROM arac_bilgileri""")
            d = imlec.fetchall()
            plaka = str(self.arac_plaka_ekle_il_kodu.text()) + " " + str(self.arac_plaka_ekle_harf.text()) + " " + str(self.arac_plaka_ekle_sayilar.text())
            tuple_plaka = tuple([plaka])
            print(tuple_plaka)
            if tuple_plaka not in d:
                veriler = [str(self.arac_marka_ekle.text()),str(self.arac_model_ekle.text()),str(self.arac_uretim_yili_ekle.text()),str(self.arac_renk_ekle.text()),str(plaka),self.arac_kullanici_id_ekle.text()]
                imlec.execute("""INSERT INTO arac_bilgileri VALUES(?,?,?,?,?,?) """,veriler)
                print(veriler)
                db.commit()
        except:
            print("Hata 2")

